import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/reports.css';

const API_BASE_URL = 'http://localhost:8000/api';

function Reports() {
  const [reportType, setReportType] = useState('project');
  const [reportName, setReportName] = useState('');
  const [projects, setProjects] = useState([]);
  const [selectedProjects, setSelectedProjects] = useState([]);
  const [lowStockOnly, setLowStockOnly] = useState(false);
  const [loading, setLoading] = useState(false);
  const [projectsLoading, setProjectsLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [generatedReports, setGeneratedReports] = useState([]);

  const token = localStorage.getItem('token');
  const config = {
    headers: { Authorization: `Bearer ${token}` }
  };

  useEffect(() => {
    fetchProjects();
    fetchGeneratedReports();
  }, []);

  // Reset selections when report type changes
  useEffect(() => {
    if (reportType !== 'project') {
      setSelectedProjects([]);
    }
    setMessage('');
  }, [reportType]);

  const fetchProjects = async () => {
    setProjectsLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/projects`, config);
      console.log('Fetched projects:', response.data);
      if (Array.isArray(response.data)) {
        setProjects(response.data);
      } else {
        console.error('Invalid projects data format:', response.data);
        setMessage('Error: Invalid project data received');
      }
    } catch (error) {
      console.error('Error fetching projects:', error);
      setMessage('Error loading projects. Please try again.');
      setProjects([]);
    } finally {
      setProjectsLoading(false);
    }
  };

  const fetchGeneratedReports = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/reports`, config);
      setGeneratedReports(response.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    }
  };

  const handleProjectSelection = (projectId) => {
    console.log('Handling project selection:', projectId);
    setMessage(''); // Clear any previous validation messages
    setSelectedProjects(prev => {
      const newSelection = prev.includes(projectId) 
        ? prev.filter(id => id !== projectId)
        : [...prev, projectId];
      console.log('Updated selection:', newSelection);
      return newSelection;
    });
  };

  const handleGenerateReport = async (e) => {
    e.preventDefault();

    if (!reportName.trim()) {
      setMessage('Please enter a report name');
      return;
    }

    if (reportType === 'project') {
      if (selectedProjects.length === 0) {
        setMessage('Please select at least one project for the project report');
        return;
      }
    }

    setLoading(true);
    setMessage('');

    const reportData = {
      report_name: reportName,
      report_type: reportType,
      project_ids: selectedProjects.length > 0 ? selectedProjects : null,
      custom_filters: {
        low_stock_only: reportType === 'inventory' ? lowStockOnly : undefined
      }
    };

    try {
      const response = await axios.post(`${API_BASE_URL}/reports/generate`, reportData, config);
      setMessage('Report generated successfully!');

      // Download the report immediately
      handleDownload(response.data.report_id);

      // Refresh the list
      fetchGeneratedReports();

      // Reset form
      setReportName('');
      setSelectedProjects([]);
    } catch (error) {
      setMessage('Error generating report: ' + (error.response?.data?.detail || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = async (reportId) => {
    try {
      const response = await axios.get(
        `${API_BASE_URL}/reports/${reportId}/download`,
        {
          ...config,
          responseType: 'blob'
        }
      );

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report_${reportId}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      setMessage('Error downloading report');
    }
  };

  const handleDelete = async (reportId) => {
    if (!window.confirm('Are you sure you want to delete this report?')) return;

    try {
      await axios.delete(`${API_BASE_URL}/reports/${reportId}`, config);
      setMessage('Report deleted successfully');
      fetchGeneratedReports();
    } catch (error) {
      setMessage('Error deleting report');
    }
  };

  return (
    <div className="reports-container">
      <h1>Report Generation</h1>

      {message && (
        <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      <div className="report-generator">
        <h2>Generate New Report</h2>

        <form onSubmit={handleGenerateReport}>
          <div className="form-group">
            <label>Report Name *</label>
            <input
              type="text"
              value={reportName}
              onChange={(e) => setReportName(e.target.value)}
              placeholder="Enter report name"
              required
            />
          </div>
          <br></br>

          <div className="form-group">
            <label>Report Type *</label>
            <select value={reportType} onChange={(e) => setReportType(e.target.value)}>
              <option value="project">Project Report</option>
              <option value="inventory">Inventory Report</option>
              <option value="task">Task Report</option>
              <option value="financial">Financial Report</option>
            </select>
          </div>
        <br></br>
          {reportType === 'project' && (
            <>
              <div className="form-group">
                <label>Select Projects *</label>
                <div className="project-selection">
                  {projects.length === 0 ? (
                    <p className="no-data">No projects available</p>
                  ) : (
                    projects.map(project => (
                      <label key={project.project_id} className="checkbox-label"> 

                        <input
                          type="checkbox"
                          checked={selectedProjects.includes(project.project_id)}
                          onChange={() => handleProjectSelection(project.project_id)}
                        />
                        <span>{project.project_name}</span>
                        <span className={`status-badge ${project.status}`}>{project.status}</span>
                      </label>
                    ))
                  )}
                </div>
              </div>


            </>
          )}

          {reportType === 'inventory' && (
            <div className="form-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={lowStockOnly}
                  onChange={(e) => setLowStockOnly(e.target.checked)}
                />
                <span>Low Stock Items Only</span>
              </label>
            </div>
          )}

          <button type="submit" disabled={loading} className="btn-generate">
            {loading ? 'Generating...' : 'Generate Report'}
          </button>
        </form>
      </div>

      <div className="generated-reports">
        <h2>Generated Reports</h2>

        {generatedReports.length === 0 ? (
          <p className="no-data">No reports generated yet</p>
        ) : (
          <table className="reports-table">
            <thead>
              <tr>
                <th>Report Name</th>
                <th>Type</th>
                <th>Generated Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {generatedReports.map(report => (
                <tr key={report.report_id}>
                  <td data-label="Report Name">{report.report_name}</td>
                  <td data-label="Type">
                    <span className={`type-badge ${report.report_type}`}>
                      {report.report_type}
                    </span>
                  </td>
                  <td data-label="Generated Date">{new Date(report.generated_at).toLocaleString()}</td>
                  <td data-label="Actions" className="actions">
                    <button 
                      onClick={() => handleDownload(report.report_id)}
                      className="btn-download"
                    >
                      Download
                    </button>
                    <button 
                      onClick={() => handleDelete(report.report_id)}
                      className="btn-delete"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Reports;
