import React, { useState, useEffect } from 'react';
import { tasksAPI, projectsAPI, usersAPI } from '../api';

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [filterProject, setFilterProject] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [showMaintenanceOnly, setShowMaintenanceOnly] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [formData, setFormData] = useState({
    project_id: '',
    task_name: '',
    description: '',
    assigned_to: '',
    status: 'pending',
    priority: 'medium',
    due_date: '',
    is_maintenance: false,
  });

  useEffect(() => {
    // Get current user from localStorage
    const userData = localStorage.getItem('user');
    if (userData) {
      setCurrentUser(JSON.parse(userData));
    }

    fetchProjects();
    fetchTasks();
    fetchUsers();
  }, []);

  useEffect(() => {
    if (filterProject) {
      fetchTasks(filterProject);
    } else {
      fetchTasks();
    }
  }, [filterProject]);

  const fetchProjects = async () => {
    try {
      const response = await projectsAPI.getAll();
      setProjects(response.data);
    } catch (err) {
      console.error('Failed to load projects');
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await usersAPI.getAll();
      setUsers(response.data);
    } catch (err) {
      console.error('Failed to load users:', err);
    }
  };

  const fetchTasks = async (projectId = null) => {
    try {
      const response = await tasksAPI.getAll(projectId);
      setTasks(response.data);
    } catch (err) {
      setError('Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Set priority based on is_maintenance checkbox
      const submitData = {
        ...formData,
        assigned_to: formData.assigned_to ? parseInt(formData.assigned_to) : null,
        project_id: parseInt(formData.project_id),
        // If maintenance is checked, set priority to high or critical
        priority: formData.is_maintenance 
          ? (formData.priority === 'low' || formData.priority === 'medium' ? 'high' : formData.priority)
          : formData.priority
      };

      // Remove is_maintenance from submitData as it's not in the backend schema
      delete submitData.is_maintenance;

      if (editingTask) {
        await tasksAPI.update(editingTask.task_id, submitData);
      } else {
        await tasksAPI.create(submitData);
      }
      fetchTasks();
      resetForm();
    } catch (err) {
      setError(err.response?.data?.detail || 'Operation failed');
    }
  };

  const handleStatusUpdate = async (taskId, newStatus) => {
    try {
      await tasksAPI.update(taskId, { status: newStatus });
      fetchTasks();
    } catch (err) {
      setError('Failed to update task status');
    }
  };

  const handleEdit = (task) => {
    setEditingTask(task);
    // Determine if task is maintenance based on priority
    const isMaintenance = task.priority === 'high' || task.priority === 'critical';
    setFormData({
      project_id: task.project_id,
      task_name: task.task_name,
      description: task.description || '',
      assigned_to: task.assigned_to || '',
      status: task.status,
      priority: task.priority,
      due_date: task.due_date ? task.due_date.split('T')[0] : '',
      is_maintenance: isMaintenance,
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await tasksAPI.delete(id);
        fetchTasks();
      } catch (err) {
        setError('Failed to delete task');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      project_id: '',
      task_name: '',
      description: '',
      assigned_to: '',
      status: 'pending',
      priority: 'medium',
      due_date: '',
      is_maintenance: false,
    });
    setEditingTask(null);
    setShowForm(false);
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      pending: 'planning',
      in_progress: 'in-progress',
      completed: 'completed',
      blocked: 'cancelled',
    };
    return <span className={`badge badge-${statusMap[status]}`}>{status.replace('_', ' ').toUpperCase()}</span>;
  };

  const getPriorityColor = (priority) => {
    const colors = {
      low: 'var(--success-green)',
      medium: 'var(--warning-orange)',
      high: 'var(--error-red)',
      critical: 'var(--error-red)',
    };
    return colors[priority] || 'var(--dark-gray)';
  };

  const getUserName = (userId) => {
    if (!userId) return 'Unassigned';
    const user = users.find(u => u.user_id === userId);
    return user ? user.full_name : 'Unknown';
  };

  const getProjectName = (projectId) => {
    const project = projects.find(p => p.project_id === projectId);
    return project ? project.project_name : 'Unknown';
  };

  // Filter users: project managers, engineers and technicians can be assigned tasks
  const getAssignableUsers = () => {
    return users.filter(user => 
      user.role === 'project_manager' || user.role === 'engineer' || user.role === 'technician'
    );
  };

  const assignableUsers = getAssignableUsers();

  const canCreateTask = currentUser && (currentUser.role === 'admin' || currentUser.role === 'project_manager');
  const canDeleteTask = currentUser && (currentUser.role === 'admin' || currentUser.role === 'project_manager');

  // Filter tasks based on user role and filters
  const getFilteredTasks = () => {
    if (!currentUser) return tasks;

    let filtered = tasks;

    // Engineers and technicians only see their assigned tasks
    if (currentUser.role === 'engineer' || currentUser.role === 'technician') {
      filtered = filtered.filter(task => task.assigned_to === currentUser.user_id);
    }

    // Filter by maintenance checkbox
    if (showMaintenanceOnly) {
      filtered = filtered.filter(task => task.priority === 'high' || task.priority === 'critical');
    }

    // Filter by status
    if (filterStatus !== 'all') {
      filtered = filtered.filter(task => task.status === filterStatus);
    }

    return filtered;
  };

  const filteredTasks = getFilteredTasks();

  // Calculate stats
  const maintenanceTasks = tasks.filter(task => task.priority === 'high' || task.priority === 'critical');
  const criticalTasks = filteredTasks.filter(task => task.priority === 'critical' && task.status !== 'completed');
  const upcomingTasks = filteredTasks.filter(task => task.status !== 'completed' && task.due_date);

  if (loading) {
    return (
      <div className="container">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px', flexWrap: 'wrap', gap: '12px' }}>
        <h1>Tasks</h1>
        {canCreateTask && (
          <button onClick={() => setShowForm(!showForm)} className="btn btn-primary">
            {showForm ? 'Cancel' : '+ New Task'}
          </button>
        )}
      </div>

      {error && <div className="alert alert-error">{error}</div>}

      {/* Stats Cards */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '16px', marginBottom: '24px' }}>
        <div className="card" style={{ textAlign: 'center', padding: '16px' }}>
          <h3 style={{ color: 'var(--navy-blue)', fontSize: '28px', margin: '0 0 8px 0' }}>{filteredTasks.length}</h3>
          <p style={{ color: 'var(--dark-gray)', fontSize: '14px', margin: 0 }}>Total Tasks</p>
        </div>
        <div className="card" style={{ textAlign: 'center', padding: '16px', backgroundColor: '#fff3cd' }}>
          <h3 style={{ color: 'var(--warning-orange)', fontSize: '28px', margin: '0 0 8px 0' }}>{criticalTasks.length}</h3>
          <p style={{ color: 'var(--dark-gray)', fontSize: '14px', margin: 0 }}>Critical Priority</p>
        </div>
        <div className="card" style={{ textAlign: 'center', padding: '16px' }}>
          <h3 style={{ color: 'var(--sea-green)', fontSize: '28px', margin: '0 0 8px 0' }}>{upcomingTasks.length}</h3>
          <p style={{ color: 'var(--dark-gray)', fontSize: '14px', margin: 0 }}>With Due Date</p>
        </div>
        <div className="card" style={{ textAlign: 'center', padding: '16px', backgroundColor: '#e8f5f1' }}>
          <h3 style={{ color: 'var(--navy-blue)', fontSize: '28px', margin: '0 0 8px 0' }}>{maintenanceTasks.length}</h3>
          <p style={{ color: 'var(--dark-gray)', fontSize: '14px', margin: 0 }}>Maintenance Tasks</p>
        </div>
      </div>

      {showForm && canCreateTask && (
        <div className="card" style={{ marginBottom: '24px' }}>
          <div className="card-header">
            <h2 className="card-title">{editingTask ? 'Edit Task' : 'Create New Task'}</h2>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Project *</label>
              <select
                name="project_id"
                className="form-select"
                value={formData.project_id}
                onChange={handleChange}
                required
              >
                <option value="">Select a project</option>
                {projects.map((project) => (
                  <option key={project.project_id} value={project.project_id}>
                    {project.project_name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Task Name *</label>
              <input
                type="text"
                name="task_name"
                className="form-input"
                value={formData.task_name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Description</label>
              <textarea
                name="description"
                className="form-textarea"
                value={formData.description}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label className="form-label">Assign To</label>
              <select
                name="assigned_to"
                className="form-select"
                value={formData.assigned_to}
                onChange={handleChange}
              >
                <option value="">Unassigned</option>
                {assignableUsers.map((user) => (
                  <option key={user.user_id} value={user.user_id}>
                    {user.full_name} ({user.role.replace('_', ' ')})
                  </option>
                ))}
              </select>
            </div>

            {/* Maintenance Checkbox */}
            <div className="form-group">
              <label style={{ display: 'flex', alignItems: 'center', gap: '8px', cursor: 'pointer' }}>
                <input
                  type="checkbox"
                  name="is_maintenance"
                  checked={formData.is_maintenance}
                  onChange={handleChange}
                />
                <span>This is a maintenance task (sets priority to High/Critical)</span>
              </label>
              <small style={{ color: 'var(--dark-gray)', display: 'block', marginTop: '4px' }}>
                Maintenance tasks are high-priority tasks that require immediate attention
              </small>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px' }}>
              <div className="form-group">
                <label className="form-label">Status</label>
                <select
                  name="status"
                  className="form-select"
                  value={formData.status}
                  onChange={handleChange}
                >
                  <option value="pending">Pending</option>
                  <option value="in_progress">In Progress</option>
                  <option value="completed">Completed</option>
                  <option value="blocked">Blocked</option>
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Priority</label>
                <select
                  name="priority"
                  className="form-select"
                  value={formData.priority}
                  onChange={handleChange}
                  disabled={formData.is_maintenance}
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                  <option value="critical">Critical</option>
                </select>
                {formData.is_maintenance && (
                  <small style={{ color: 'var(--dark-gray)', marginTop: '4px', display: 'block' }}>
                    Priority is automatically set to High for maintenance tasks
                  </small>
                )}
              </div>

              <div className="form-group">
                <label className="form-label">Due Date</label>
                <input
                  type="date"
                  name="due_date"
                  className="form-input"
                  value={formData.due_date}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div style={{ display: 'flex', gap: '12px' }}>
              <button type="submit" className="btn btn-primary">
                {editingTask ? 'Update Task' : 'Create Task'}
              </button>
              <button type="button" onClick={resetForm} className="btn btn-secondary">
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Filters */}
      <div style={{ display: 'flex', gap: '12px', marginBottom: '24px', flexWrap: 'wrap', alignItems: 'center' }}>
        <select
          value={filterProject}
          onChange={(e) => setFilterProject(e.target.value)}
          className="form-select"
          style={{ width: '200px' }}
        >
          <option value="">All Projects</option>
          {projects.map((project) => (
            <option key={project.project_id} value={project.project_id}>
              {project.project_name}
            </option>
          ))}
        </select>

        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="form-select"
          style={{ width: '180px' }}
        >
          <option value="all">All Statuses</option>
          <option value="pending">Pending</option>
          <option value="in_progress">In Progress</option>
          <option value="completed">Completed</option>
          <option value="blocked">Blocked</option>
        </select>

        <label style={{ display: 'flex', alignItems: 'center', gap: '8px', padding: '8px 16px', backgroundColor: 'white', borderRadius: '8px', border: '1px solid var(--light-gray)', cursor: 'pointer' }}>
          <input
            type="checkbox"
            checked={showMaintenanceOnly}
            onChange={(e) => setShowMaintenanceOnly(e.target.checked)}
          />
          <span style={{ fontSize: '14px', fontWeight: '500' }}>Show Maintenance Only</span>
        </label>
      </div>

      {filteredTasks.length === 0 ? (
        <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
          <h3 style={{ color: 'var(--dark-gray)' }}>No tasks found</h3>
          {canCreateTask ? (
            <p>Click "New Task" to create your first task</p>
          ) : (
            <p>No tasks assigned to you yet</p>
          )}
        </div>
      ) : (
        <div className="card">
          {/* Horizontal scroll wrapper */}
          <div style={{ overflowX: 'auto', width: '100%' }}>
            <table style={{ width: '100%', minWidth: '900px' }}>
              <thead>
                <tr>
                  <th style={{ minWidth: '200px' }}>Task</th>
                  <th style={{ minWidth: '150px' }}>Project</th>
                  <th style={{ minWidth: '130px' }}>Assigned To</th>
                  <th style={{ minWidth: '120px' }}>Status</th>
                  <th style={{ minWidth: '100px' }}>Priority</th>
                  <th style={{ minWidth: '120px' }}>Due Date</th>
                  <th style={{ minWidth: '150px' }}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredTasks.map((task) => (
                  <tr key={task.task_id}>
                    <td>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                        {(task.priority === 'high' || task.priority === 'critical') && (
                          <span style={{ 
                            backgroundColor: 'var(--error-red)', 
                            color: 'white', 
                            padding: '2px 6px', 
                            borderRadius: '4px', 
                            fontSize: '10px',
                            fontWeight: 'bold'
                          }}>
                            MAINT
                          </span>
                        )}
                        <div>
                          <strong>{task.task_name}</strong>
                          {task.description && (
                            <div style={{ fontSize: '12px', color: 'var(--dark-gray)', marginTop: '4px' }}>
                              {task.description.substring(0, 80)}{task.description.length > 80 ? '...' : ''}
                            </div>
                          )}
                        </div>
                      </div>
                    </td>
                    <td>{getProjectName(task.project_id)}</td>
                    <td>{getUserName(task.assigned_to)}</td>
                    <td>{getStatusBadge(task.status)}</td>
                    <td>
                      <span style={{ color: getPriorityColor(task.priority), fontWeight: 'bold', fontSize: '13px' }}>
                        {task.priority.toUpperCase()}
                      </span>
                    </td>
                    <td>{task.due_date ? new Date(task.due_date).toLocaleDateString() : 'N/A'}</td>
                    <td>
                      <div style={{ display: 'flex', gap: '8px', alignItems: 'center', flexWrap: 'wrap' }}>
                        {!canCreateTask ? (
                          // Engineers and technicians can only update status
                          <select
                            value={task.status}
                            onChange={(e) => handleStatusUpdate(task.task_id, e.target.value)}
                            className="form-select"
                            style={{ padding: '6px 12px', fontSize: '12px', minWidth: '130px' }}
                          >
                            <option value="pending">Pending</option>
                            <option value="in_progress">In Progress</option>
                            <option value="completed">Completed</option>
                            <option value="blocked">Blocked</option>
                          </select>
                        ) : (
                          // Admins and project managers can edit the full task
                          <>
                            <button onClick={() => handleEdit(task)} className="btn btn-secondary" style={{ padding: '6px 12px', fontSize: '12px', whiteSpace: 'nowrap' }}>
                              Edit
                            </button>
                            {canDeleteTask && (
                              <button onClick={() => handleDelete(task.task_id)} className="btn btn-danger" style={{ padding: '6px 12px', fontSize: '12px', whiteSpace: 'nowrap' }}>
                                Delete
                              </button>
                            )}
                          </>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

export default Tasks;
