import React, { useState, useEffect } from 'react';
import { projectsAPI, tasksAPI, usersAPI } from '../api';
import '../styles/maintenance.css';

function Maintenance() {
  const [projects, setProjects] = useState([]);
  const [users, setUsers] = useState([]);
  const [maintenanceTasks, setMaintenanceTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [currentUser, setCurrentUser] = useState(null);

  const [formData, setFormData] = useState({
    project_id: '',
    task_name: '',
    description: '',
    assigned_to: '',
    priority: 'medium',
    status: 'pending',
    due_date: ''
  });

  useEffect(() => {
    // Get current user from localStorage
    const userData = localStorage.getItem('user');
    if (userData) {
      setCurrentUser(JSON.parse(userData));
    }
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [projectsRes, tasksRes, usersRes] = await Promise.all([
        projectsAPI.getAll(),
        tasksAPI.getAll(),
        usersAPI.getAll()
      ]);
      setProjects(projectsRes.data);
      setUsers(usersRes.data);
      // Filter only maintenance-related tasks (or all tasks for now)
      setMaintenanceTasks(tasksRes.data);
    } catch (err) {
      console.error('Failed to load maintenance data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Prepare data - convert assigned_to to int if not empty
      const submitData = {
        ...formData,
        assigned_to: formData.assigned_to ? parseInt(formData.assigned_to) : null,
        project_id: parseInt(formData.project_id)
      };
      await tasksAPI.create(submitData);
      loadData();
      closeModal();
    } catch (err) {
      console.error('Failed to create maintenance task:', err);
      alert('Failed to create maintenance task');
    }
  };

  const handleUpdateStatus = async (taskId, newStatus) => {
    try {
      await tasksAPI.update(taskId, { status: newStatus });
      loadData();
    } catch (err) {
      console.error('Failed to update task:', err);
    }
  };

  const handleDelete = async (taskId) => {
    if (window.confirm('Are you sure you want to delete this maintenance record?')) {
      try {
        await tasksAPI.delete(taskId);
        loadData();
      } catch (err) {
        console.error('Failed to delete task:', err);
      }
    }
  };

  const openModal = () => {
    setFormData({
      project_id: '',
      task_name: '',
      description: '',
      assigned_to: '',
      priority: 'medium',
      status: 'pending',
      due_date: ''
    });
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  // Filter users: only engineers and technicians can be assigned maintenance tasks
  const getAssignableUsers = () => {
    return users.filter(user => 
      user.role === 'engineer' || user.role === 'technician'
    );
  };

  const getUserName = (userId) => {
    if (!userId) return 'Unassigned';
    const user = users.find(u => u.user_id === userId);
    return user ? user.full_name : 'Unknown';
  };

  const assignableUsers = getAssignableUsers();
  const canManageMaintenance = currentUser && (currentUser.role === 'admin' || currentUser.role === 'project_manager');

  const filterTasks = () => {
    let filtered = maintenanceTasks;

    // If user is engineer or technician, only show their assigned tasks
    if (currentUser && (currentUser.role === 'engineer' || currentUser.role === 'technician')) {
      filtered = filtered.filter(task => task.assigned_to === currentUser.user_id);
    }

    if (selectedProject !== 'all') {
      filtered = filtered.filter(task => task.project_id === parseInt(selectedProject));
    }

    if (filterStatus !== 'all') {
      filtered = filtered.filter(task => task.status === filterStatus);
    }

    return filtered;
  };

  const filteredTasks = filterTasks();
  const upcomingMaintenance = filteredTasks.filter(task => 
    task.status !== 'completed' && task.due_date
  ).sort((a, b) => new Date(a.due_date) - new Date(b.due_date));

  const criticalMaintenance = filteredTasks.filter(task => 
    task.priority === 'critical' && task.status !== 'completed'
  );

  const completedMaintenance = filteredTasks.filter(task => 
    task.status === 'completed'
  );

  const getStatusClass = (status) => {
    const statusMap = {
      'pending': 'status-pending',
      'in_progress': 'status-progress',
      'completed': 'status-completed',
      'blocked': 'status-blocked'
    };
    return statusMap[status] || '';
  };

  const getPriorityClass = (priority) => {
    return `priority-${priority}`;
  };

  if (loading) {
    return <div className="loading">Loading maintenance data...</div>;
  }

  return (
    <div className="maintenance-container">
      <div className="maintenance-header">
        <h1>Maintenance Management</h1>
        {canManageMaintenance && (
          <button className="btn-primary" onClick={openModal}>
            + Log Maintenance
          </button>
        )}
      </div>

      <div className="maintenance-stats">
        <div className="stat-card">
          <h3>Total Tasks</h3>
          <p className="stat-value">{maintenanceTasks.length}</p>
        </div>
        <div className="stat-card warning">
          <h3>Critical</h3>
          <p className="stat-value">{criticalMaintenance.length}</p>
        </div>
        <div className="stat-card">
          <h3>Upcoming</h3>
          <p className="stat-value">{upcomingMaintenance.length}</p>
        </div>
        <div className="stat-card success">
          <h3>Completed</h3>
          <p className="stat-value">{completedMaintenance.length}</p>
        </div>
      </div>

      <div className="maintenance-filters">
        <select 
          value={selectedProject}
          onChange={(e) => setSelectedProject(e.target.value)}
          className="filter-select"
        >
          <option value="all">All Projects</option>
          {projects.map(project => (
            <option key={project.project_id} value={project.project_id}>
              {project.project_name}
            </option>
          ))}
        </select>

        <select 
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="filter-select"
        >
          <option value="all">All Statuses</option>
          <option value="pending">Pending</option>
          <option value="in_progress">In Progress</option>
          <option value="completed">Completed</option>
          <option value="blocked">Blocked</option>
        </select>
      </div>

      <div className="maintenance-content">
        {upcomingMaintenance.length > 0 && (
          <div className="section">
            <h2>Upcoming Maintenance</h2>
            <div className="maintenance-cards">
              {upcomingMaintenance.slice(0, 5).map(task => {
                const project = projects.find(p => p.project_id === task.project_id);
                const daysUntil = Math.ceil((new Date(task.due_date) - new Date()) / (1000 * 60 * 60 * 24));

                return (
                  <div key={task.task_id} className={`maintenance-card ${getPriorityClass(task.priority)}`}>
                    <div className="card-header">
                      <h3>{task.task_name}</h3>
                      <div className="badges">
                        <span className={`priority-badge ${getPriorityClass(task.priority)}`}>
                          {task.priority}
                        </span>
                        <span className={`status-badge ${getStatusClass(task.status)}`}>
                          {task.status.replace('_', ' ')}
                        </span>
                      </div>
                    </div>
                    <div className="card-body">
                      <p className="project-name">Project: {project?.project_name || 'N/A'}</p>
                      {project && <p className="vessel-name">Vessel: {project.vessel_name || 'N/A'}</p>}
                      <p className="description">{task.description || 'No description'}</p>
                      <p className={`due-date ${daysUntil <= 3 ? 'urgent' : ''}`}>
                        Due: {task.due_date} ({daysUntil} days)
                      </p>
                    </div>
                    <div className="card-actions">
                      <button 
                        className="btn-start"
                        onClick={() => handleUpdateStatus(task.task_id, 'in_progress')}
                        disabled={task.status === 'in_progress' || task.status === 'completed'}
                      >
                        Start
                      </button>
                      <button 
                        className="btn-complete"
                        onClick={() => handleUpdateStatus(task.task_id, 'completed')}
                        disabled={task.status === 'completed'}
                      >
                        Complete
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        <div className="section">
          <h2>All Maintenance Records</h2>
          <div className="maintenance-table-container">
            <table className="maintenance-table">
              <thead>
                <tr>
                  <th>Task</th>
                  <th>Project</th>
                  <th>Vessel</th>
                  <th>Assigned To</th>
                  <th>Priority</th>
                  <th>Status</th>
                  <th>Due Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredTasks.map(task => {
                  const project = projects.find(p => p.project_id === task.project_id);
                  return (
                    <tr key={task.task_id}>
                      <td>{task.task_name}</td>
                      <td>{project?.project_name || 'N/A'}</td>
                      <td>{project?.vessel_name || 'N/A'}</td>
                      <td>{getUserName(task.assigned_to)}</td>
                      <td>
                        <span className={`priority-badge ${getPriorityClass(task.priority)}`}>
                          {task.priority}
                        </span>
                      </td>
                      <td>
                        <span className={`status-badge ${getStatusClass(task.status)}`}>
                          {task.status.replace('_', ' ')}
                        </span>
                      </td>
                      <td>{task.due_date || 'N/A'}</td>
                      <td className="actions">
                        <select 
                          value={task.status}
                          onChange={(e) => handleUpdateStatus(task.task_id, e.target.value)}
                          className="status-select"
                        >
                          <option value="pending">Pending</option>
                          <option value="in_progress">In Progress</option>
                          <option value="completed">Completed</option>
                          <option value="blocked">Blocked</option>
                        </select>
                        {canManageMaintenance && (
                          <button 
                            className="btn-delete"
                            onClick={() => handleDelete(task.task_id)}
                          >
                            Delete
                          </button>
                        )}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {showModal && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Log New Maintenance</h2>
              <button className="modal-close" onClick={closeModal}></button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Project *</label>
                <select
                  value={formData.project_id}
                  onChange={(e) => setFormData({...formData, project_id: parseInt(e.target.value)})}
                  required
                >
                  <option value="">Select Project</option>
                  {projects.map(project => (
                    <option key={project.project_id} value={project.project_id}>
                      {project.project_name} {project.vessel_name && `- ${project.vessel_name}`}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Task Name *</label>
                <input
                  type="text"
                  value={formData.task_name}
                  onChange={(e) => setFormData({...formData, task_name: e.target.value})}
                  required
                  placeholder="e.g., Engine Oil Change, Hull Inspection"
                />
              </div>

              <div className="form-group">
                <label>Description</label>
                <textarea
                  value={formData.description}
                  onChange={(e) => setFormData({...formData, description: e.target.value})}
                  rows="4"
                  placeholder="Details about the maintenance task..."
                />
              </div>

              <div className="form-group">
                <label>Assign To (Engineer/Technician)</label>
                <select
                  value={formData.assigned_to}
                  onChange={(e) => setFormData({...formData, assigned_to: e.target.value})}
                >
                  <option value="">Unassigned</option>
                  {assignableUsers.map((user) => (
                    <option key={user.user_id} value={user.user_id}>
                      {user.full_name} ({user.role.replace('_', ' ')})
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Priority *</label>
                  <select
                    value={formData.priority}
                    onChange={(e) => setFormData({...formData, priority: e.target.value})}
                  >
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                    <option value="critical">Critical</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Due Date</label>
                  <input
                    type="date"
                    value={formData.due_date}
                    onChange={(e) => setFormData({...formData, due_date: e.target.value})}
                  />
                </div>
              </div>

              <div className="modal-actions">
                <button type="button" className="btn-secondary" onClick={closeModal}>
                  Cancel
                </button>
                <button type="submit" className="btn-primary">
                  Create Task
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Maintenance;
