import React, { useState, useEffect } from 'react';
import { inventoryAPI, projectsAPI } from '../api';

function Inventory() {
  const [inventory, setInventory] = useState([]);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [showLowStock, setShowLowStock] = useState(false);
  const [formData, setFormData] = useState({
    project_id: '',
    item_name: '',
    description: '',
    quantity: 0,
    unit: '',
    unit_cost: '',
    supplier: '',
    minimum_stock: 0,
    location: '',
  });

  useEffect(() => {
    fetchProjects();
    fetchInventory();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await projectsAPI.getAll();
      setProjects(response.data);
    } catch (err) {
      console.error('Failed to load projects');
    }
  };

  const fetchInventory = async () => {
    try {
      const response = showLowStock ? await inventoryAPI.getLowStock() : await inventoryAPI.getAll();
      setInventory(response.data);
    } catch (err) {
      setError('Failed to load inventory');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInventory();
  }, [showLowStock]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const submitData = {
        ...formData,
        project_id: formData.project_id || null,
      };

      if (editingItem) {
        await inventoryAPI.update(editingItem.id, submitData);
      } else {
        await inventoryAPI.create(submitData);
      }
      fetchInventory();
      resetForm();
    } catch (err) {
      setError(err.response?.data?.detail || 'Operation failed');
    }
  };

  const handleEdit = (item) => {
    setEditingItem(item);
    setFormData({
      project_id: item.project_id || '',
      item_name: item.item_name,
      description: item.description || '',
      quantity: item.quantity,
      unit: item.unit || '',
      unit_cost: item.unit_cost || '',
      supplier: item.supplier || '',
      minimum_stock: item.minimum_stock,
      location: item.location || '',
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this inventory item?')) {
      try {
        await inventoryAPI.delete(id);
        fetchInventory();
      } catch (err) {
        setError('Failed to delete inventory item');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      project_id: '',
      item_name: '',
      description: '',
      quantity: 0,
      unit: '',
      unit_cost: '',
      supplier: '',
      minimum_stock: 0,
      location: '',
    });
    setEditingItem(null);
    setShowForm(false);
  };

  const isLowStock = (item) => {
    return item.quantity <= item.minimum_stock;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
        <h1>Inventory Management</h1>
        <div style={{ display: 'flex', gap: '12px' }}>
          <button
            onClick={() => setShowLowStock(!showLowStock)}
            className={showLowStock ? "btn btn-danger" : "btn btn-secondary"}
          >
            {showLowStock ? 'Show All' : 'Low Stock Only'}
          </button>
          <button onClick={() => setShowForm(!showForm)} className="btn btn-primary">
            {showForm ? 'Cancel' : '+ New Item'}
          </button>
        </div>
      </div>

      {error && <div className="alert alert-error">{error}</div>}

      {showForm && (
        <div className="card" style={{ marginBottom: '24px' }}>
          <div className="card-header">
            <h2 className="card-title">{editingItem ? 'Edit Inventory Item' : 'Add New Inventory Item'}</h2>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Project (Optional)</label>
              <select
                name="project_id"
                className="form-select"
                value={formData.project_id}
                onChange={handleChange}
              >
                <option value="">No Project (General Inventory)</option>
                {projects.map((project) => (
                  <option key={project.id} value={project.id}>
                    {project.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Item Name *</label>
              <input
                type="text"
                name="item_name"
                className="form-input"
                value={formData.item_name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Description</label>
              <textarea
                name="description"
                className="form-textarea"
                value={formData.description}
                onChange={handleChange}
              />
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
              <div className="form-group">
                <label className="form-label">Quantity *</label>
                <input
                  type="number"
                  name="quantity"
                  className="form-input"
                  value={formData.quantity}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Unit</label>
                <input
                  type="text"
                  name="unit"
                  className="form-input"
                  placeholder="e.g., pcs, kg, liters"
                  value={formData.unit}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
              <div className="form-group">
                <label className="form-label">Unit Cost ($)</label>
                <input
                  type="number"
                  name="unit_cost"
                  className="form-input"
                  value={formData.unit_cost}
                  onChange={handleChange}
                  step="0.01"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Minimum Stock Level *</label>
                <input
                  type="number"
                  name="minimum_stock"
                  className="form-input"
                  value={formData.minimum_stock}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
              <div className="form-group">
                <label className="form-label">Supplier</label>
                <input
                  type="text"
                  name="supplier"
                  className="form-input"
                  value={formData.supplier}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Location</label>
                <input
                  type="text"
                  name="location"
                  className="form-input"
                  placeholder="e.g., Warehouse A, Shelf 12"
                  value={formData.location}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div style={{ display: 'flex', gap: '12px' }}>
              <button type="submit" className="btn btn-primary">
                {editingItem ? 'Update Item' : 'Add Item'}
              </button>
              <button type="button" onClick={resetForm} className="btn btn-secondary">
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {inventory.length === 0 ? (
        <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
          <h3 style={{ color: 'var(--dark-gray)' }}>No inventory items yet</h3>
          <p>Click "New Item" to add your first inventory item</p>
        </div>
      ) : (
        <div className="card">
          <table>
            <thead>
              <tr>
                <th>Item Name</th>
                <th>Quantity</th>
                <th>Unit Cost</th>
                <th>Total Value</th>
                <th>Location</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {inventory.map((item) => (
                <tr key={item.id} style={{ backgroundColor: isLowStock(item) ? '#FFF3E0' : 'transparent' }}>
                  <td>
                    <strong>{item.item_name}</strong>
                    {item.description && (
                      <div style={{ fontSize: '12px', color: 'var(--dark-gray)', marginTop: '4px' }}>
                        {item.description.substring(0, 50)}{item.description.length > 50 ? '...' : ''}
                      </div>
                    )}
                  </td>
                  <td>
                    {item.quantity} {item.unit}
                    <div style={{ fontSize: '12px', color: 'var(--dark-gray)' }}>
                      Min: {item.minimum_stock}
                    </div>
                  </td>
                  <td>{item.unit_cost ? `$${Number(item.unit_cost).toFixed(2)}` : 'N/A'}</td>
                  <td>
                    {item.unit_cost ? 
                      <strong>${(item.quantity * item.unit_cost).toFixed(2)}</strong> : 
                      'N/A'
                    }
                  </td>
                  <td>{item.location || 'N/A'}</td>
                  <td>
                    {isLowStock(item) ? (
                      <span className="badge badge-on-hold">LOW STOCK</span>
                    ) : (
                      <span className="badge badge-completed">IN STOCK</span>
                    )}
                  </td>
                  <td>
                    <div style={{ display: 'flex', gap: '8px' }}>
                      <button onClick={() => handleEdit(item)} className="btn btn-secondary" style={{ padding: '6px 12px', fontSize: '12px' }}>
                        Edit
                      </button>
                      <button onClick={() => handleDelete(item.id)} className="btn btn-danger" style={{ padding: '6px 12px', fontSize: '12px' }}>
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Inventory;
