import React, { useState, useEffect } from 'react';
import { projectsAPI } from '../api';

function Projects() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingProject, setEditingProject] = useState(null);
  const [formData, setFormData] = useState({
    project_name: '',
    description: '',
    vessel_name: '',
    vessel_type: '',
    owner_name: '',
    status: 'planning',
    start_date: '',
    end_date: '',
    budget: '',
  });

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await projectsAPI.getAll();
      setProjects(response.data);
    } catch (err) {
      setError('Failed to load projects');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      if (editingProject) {
        await projectsAPI.update(editingProject.project_id, formData);
      } else {
        await projectsAPI.create(formData);
      }
      fetchProjects();
      resetForm();
    } catch (err) {
      setError(err.response?.data?.detail || 'Operation failed');
    }
  };

  const handleEdit = (project) => {
    setEditingProject(project);
    setFormData({
      project_name: project.project_name,
      description: project.description || '',
      vessel_name: project.vessel_name || '',
      vessel_type: project.vessel_type || '',
      owner_name: project.owner_name || '',
      status: project.status,
      start_date: project.start_date ? project.start_date.split('T')[0] : '',
      end_date: project.end_date ? project.end_date.split('T')[0] : '',
      budget: project.budget || '',
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this project?')) {
      try {
        await projectsAPI.delete(id);
        fetchProjects();
      } catch (err) {
        setError('Failed to delete project');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      project_name: '',
      description: '',
      vessel_name: '',
      vessel_type: '',
      owner_name: '',
      status: 'planning',
      start_date: '',
      end_date: '',
      budget: '',
    });
    setEditingProject(null);
    setShowForm(false);
  };

  const getStatusBadge = (status) => {
    return <span className={`badge badge-${status.replace('_', '-')}`}>{status.replace('_', ' ').toUpperCase()}</span>;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
        <h1>Projects</h1>
        <button onClick={() => setShowForm(!showForm)} className="btn btn-primary">
          {showForm ? 'Cancel' : '+ New Project'}
        </button>
      </div>

      {error && <div className="alert alert-error">{error}</div>}

      {showForm && (
        <div className="card" style={{ marginBottom: '24px' }}>
          <div className="card-header">
            <h2 className="card-title">{editingProject ? 'Edit Project' : 'Create New Project'}</h2>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Project Name *</label>
              <input
                type="text"
                name="project_name"
                className="form-input"
                value={formData.project_name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Description</label>
              <textarea
                name="description"
                className="form-textarea"
                value={formData.description}
                onChange={handleChange}
              />
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px' }}>
              <div className="form-group">
                <label className="form-label">Vessel Name</label>
                <input
                  type="text"
                  name="vessel_name"
                  className="form-input"
                  value={formData.vessel_name}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Vessel Type</label>
                <input
                  type="text"
                  name="vessel_type"
                  className="form-input"
                  value={formData.vessel_type}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Owner Name</label>
                <input
                  type="text"
                  name="owner_name"
                  className="form-input"
                  value={formData.owner_name}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px' }}>
              <div className="form-group">
                <label className="form-label">Status</label>
                <select
                  name="status"
                  className="form-select"
                  value={formData.status}
                  onChange={handleChange}
                >
                  <option value="planning">Planning</option>
                  <option value="in_progress">In Progress</option>
                  <option value="on_hold">On Hold</option>
                  <option value="completed">Completed</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Start Date</label>
                <input
                  type="date"
                  name="start_date"
                  className="form-input"
                  value={formData.start_date}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label className="form-label">End Date</label>
                <input
                  type="date"
                  name="end_date"
                  className="form-input"
                  value={formData.end_date}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">Budget ($)</label>
              <input
                type="number"
                name="budget"
                className="form-input"
                value={formData.budget}
                onChange={handleChange}
                step="0.01"
              />
            </div>

            <div style={{ display: 'flex', gap: '12px' }}>
              <button type="submit" className="btn btn-primary">
                {editingProject ? 'Update Project' : 'Create Project'}
              </button>
              <button type="button" onClick={resetForm} className="btn btn-secondary">
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {projects.length === 0 ? (
        <div className="card" style={{ textAlign: 'center', padding: '40px' }}>
          <h3 style={{ color: 'var(--dark-gray)' }}>No projects yet</h3>
          <p>Click "New Project" to create your first project</p>
        </div>
      ) : (
        <div className="card">
          <table>
            <thead>
              <tr>
                <th>Project Name</th>
                <th>Vessel</th>
                <th>Status</th>
                <th>Budget</th>
                <th>Start Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {projects.map((project) => (
                <tr key={project.project_id}>
                  <td>
                    <strong>{project.project_name}</strong>
                    {project.description && (
                      <div style={{ fontSize: '12px', color: 'var(--dark-gray)', marginTop: '4px' }}>
                        {project.description.substring(0, 60)}{project.description.length > 60 ? '...' : ''}
                      </div>
                    )}
                  </td>
                  <td>{project.vessel_name || 'N/A'}</td>
                  <td>{getStatusBadge(project.status)}</td>
                  <td>{project.budget ? `$${Number(project.budget).toLocaleString()}` : 'N/A'}</td>
                  <td>{project.start_date ? new Date(project.start_date).toLocaleDateString() : 'N/A'}</td>
                  <td>
                    <div style={{ display: 'flex', gap: '8px' }}>
                      <button onClick={() => handleEdit(project)} className="btn btn-secondary" style={{ padding: '6px 12px', fontSize: '12px' }}>
                        Edit
                      </button>
                      <button onClick={() => handleDelete(project.project_id)} className="btn btn-danger" style={{ padding: '6px 12px', fontSize: '12px' }}>
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Projects;
